// Taxi Rate Code
val map_rateCode = spark.read.format("csv")
      .schema(schema_taxi_rate_code)
      .option("delimiter", ",")
      .option("header", "true")
      .load("/databricks-datasets/nyctaxi/taxizone/taxi_rate_code.csv")

// Taxi Payment Type
val map_paymentType = spark.read.format("csv")
