val gold_summaryStats = silver_GreenCab
    .groupBy("do_date").agg(
        expr("COUNT(DISTINCT pu_datetime) AS pickups"), 
        expr("COUNT(DISTINCT do_datetime) AS dropoffs"),
        expr("COUNT(1) AS trips")
      )

display(gold_summaryStats)

